<?phpob_start();include("db.php");set_time_limit(0);    ini_set('display_errors', true); error_reporting(E_ALL);$username=$_POST['username']; $password=$_POST['password'];//for security$username = @$db->real_escape_string($username);$password = @$db->real_escape_string($password);$username = @strip_tags($username); $password = @strip_tags($password); $username = @stripslashes($username);$password = @stripslashes($password);$password = sha1($password);$q = "SELECT * FROM members WHERE username=? and password=? LIMIT 1";if ($stmt = $db->prepare($q)) {    $stmt->bind_param("ss", $username, $password);    $stmt->execute();    $stmt->store_result();       $count = $stmt->num_rows;    $stmt->close();}if($count==1){    echo 1;} else {    echo "Wrong Username or Password";}ob_end_flush();?>