<?phpob_start();include("db.php");set_time_limit(0);    ini_set('display_errors', true); error_reporting(E_ALL);$username=$_POST['username']; $password=$_POST['password'];$username = $db->real_escape_string($username);$password = $db->real_escape_string($password);$q = "SELECT * FROM members WHERE username=? and password=?";if ($stmt = $db->prepare($q)) {    $stmt->bind_param("ss", $username, $password);    $stmt->execute();    $stmt->store_result();       $count = $stmt->num_rows;    $stmt->close();}if($count==1){    session_register("username");    session_register("password");     header("location:login_success.php");} else {    echo "Wrong Username or Password";}ob_end_flush();?>